type ReplicaState = { nodeId: string; lagMs: number };

export function pickPrimary(replicas: ReplicaState[]): ReplicaState | null {
  if (replicas.length === 0) return null;
  return replicas.reduce((best, next) => (next.lagMs < best.lagMs ? next : best));
}

export function getFocusable(root: HTMLElement): HTMLElement[] {
  const selector = 'button, [href], input, select, textarea, [tabindex]:not([tabindex="-1"])';
  return Array.from(root.querySelectorAll<HTMLElement>(selector)).filter((el) => !el.hasAttribute('disabled'));
}
