type StreamMessage = { channel: string; payload: unknown };

export function connectStream(url: string, onMessage: (msg: StreamMessage) => void) {
  const socket = new WebSocket(url);

  socket.addEventListener('message', (event) => {
    const parsed = JSON.parse(event.data) as StreamMessage;
    onMessage(parsed);
  });

  return () => socket.close();
}
